class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n = v.size();
        int m = v[0].size();
        int ans = 0;
        for(int i = 0;i<n/2;i++){
            for(int j = 0;j<m/2;j++){
                int one = 0;
                if(v[i][j]==1) one++;
                if(v[i][m-j-1]==1) one++;
                if(v[n-i-1][j]==1) one++;
                if(v[n-i-1][m-j-1]==1) one++;
                ans += min(one, 4-one);
            }
        }
        int cnt = 0;
        int one = 0;
        if(n%2==1 && m%2==1){
            if(v[n/2][m/2]==1) ans++;
            for(int i = 0;i<n/2;i++){
                if(v[i][m/2]!=v[n-i-1][m/2]) cnt++;
                else if(v[i][m/2]==1) one += 2;
            }
            for(int i = 0;i<m/2;i++){
                if(v[n/2][i]!=v[n/2][m-i-1]) cnt++;
                else if(v[n/2][i]==1) one += 2;
            }
            if(one%4+cnt*2 >= 4) ans += cnt;
            else ans += (one%4+cnt);
        }
        else if(n%2==1){
            for(int i = 0;i<m/2;i++){
                if(v[n/2][i]!=v[n/2][m-i-1]) cnt++;
                else if(v[n/2][i]==1) one += 2; 
            }
            if(one%4+cnt*2 >= 4) ans += cnt;
            else ans += (one%4+cnt);         
        }
        else if(m%2==1){
            for(int i = 0;i<n/2;i++){
                if(v[i][m/2]!=v[n-i-1][m/2]) cnt++;
                else if(v[i][m/2]==1) one += 2;
            }
            if(one%4+cnt*2 >= 4) ans += cnt;
            else ans += (one%4+cnt); 
        }
        return ans;

    }
};
