class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        l1 = 0
        r1 = m - 1
        res = 0
        while l1 < r1:
            l2 = 0
            r2 = n - 1
            while l2 < r2:
                s = grid[l1][l2] + grid[l1][r2] + grid[r1][l2] + grid[r1][r2]
                if s > 2:
                    res += 4 - s
                else:
                    res += s
                l2 += 1
                r2 -= 1
            l1 += 1
            r1 -= 1
        q = 0
        p = 0
        mid = 0
        if m % 2 == 1:
            l = 0
            r = n - 1
            k = m // 2
            while l < r:
                s = grid[k][l] + grid[k][r] 
                if s == 2:
                    q += 1
                if s == 1:
                    p += 1
                l += 1
                r -= 1
            if l == r:
                mid = grid[k][l]
        if n % 2 == 1:
            l = 0
            r = m - 1
            k = n // 2
            while l < r:
                s = grid[l][k] + grid[r][k] 
                if s == 2:
                    q += 1
                if s == 1:
                    p += 1
                l += 1
                r -= 1

        q = q % 2
        if q == 1:
            if p == 0:
                res += 2
            else:
                res += p
        else:
            res += p
        return res + mid
