class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;

        int ans = 0;
        for (int i=0; i<n/2; i++)
            for (int j=0; j<m/2; j++) {
                int a = grid[i][j];
                int b = grid[n-i-1][j];
                int c = grid[i][m-j-1];
                int d = grid[n-i-1][m-j-1];
                int count1 = a + b + c + d;
                int count2 = 4 - count1;
                ans += Math.min(count1, count2);
// System.out.print("At grid[" + i + "][" + j + "] -> ");
                if (count1 <= count2) {
                    grid[i][j] = 0;
                    grid[n-i-1][j] = 0;
                    grid[i][m-j-1] = 0;
                    grid[n-i-1][m-j-1] = 0;
// System.out.println("count1=" + count1);
                } else {
                    grid[i][j] = 1;
                    grid[n-i-1][j] = 1;
                    grid[i][m-j-1] = 1;
                    grid[n-i-1][m-j-1] = 1;
// System.out.println("count2=" + count2);
                }
            }

        int addOrDelete = 0;
        int pairs = 0;
        if (n%2 == 1) {
            for (int j=0; j<m/2; j++) {
                int a = grid[n/2][j];
                int b = grid[n/2][m-j-1];
                int count1 = a + b;
                if (count1 == 1)
                    addOrDelete++;
                else if (count1 == 2)
                    pairs++;
            }
        }
        if (m%2 == 1) {
            for (int i=0; i<n/2; i++) {
                int a = grid[i][m/2];
                int b = grid[n-i-1][m/2];
                int count1 = a + b;
                if (count1 == 1)
                    addOrDelete++;
                else if (count1 == 2)
                    pairs++;
            }
        }
        if (n%2 == 1 && m%2 == 1 && grid[n/2][m/2] == 1)
            ans++;

        if (pairs%2 == 1 && addOrDelete == 0)
            ans += 2;

        ans += addOrDelete;

        return ans;
    }

}
