class Solution {
    public int minFlips(int[][] grid) {
		int m=grid.length;
		int n=grid[0].length;

		int res=0;

		//四个角
		for(int i=0;i<m/2;i++) {
			for(int j=0;j<n/2;j++) {
				int cnt=0;//统计0
				if(grid[i][j]==0) cnt++;
				if(grid[i][n-1-j]==0) cnt++;
				if(grid[m-1-i][j]==0) cnt++;
				if(grid[m-1-i][n-1-j]==0) cnt++;
				res+=Math.min(cnt, 4-cnt);
			}
		}

		//对称轴
		int t=0;
		int cnt=0;

		if(m%2==1) {
			int row=m/2;
			for(int j=0;j<n/2;j++) {
				if(grid[row][j]!=grid[row][n-1-j]) t++;
				else if(t==0&&grid[row][j]==1) cnt++;
			}
		}
		if(n%2==1) {
			int column=n/2;
			for(int i=0;i<m/2;i++) {
				if(grid[i][column]!=grid[m-1-i][column]) t++;
				else if(t==0&&grid[i][column]==1) cnt++;
			}
		}

		res+=t;
		if(t==0&&cnt%2!=0) res+=2;
		if(m%2==1&&n%2==1) {
			if(grid[m/2][n/2]==1) res++;
		}
		return res;
    }
}
