class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int m=grid.size(),n=grid[0].size();
        for(int i=0;i<m/2;++i){
            for(int j=0;j<n/2;++j){
                int onec=0;
                if(grid[i][j]) ++onec;
                if(grid[i][n-1-j]) ++onec;
                if(grid[m-1-i][j]) ++onec;
                if(grid[m-1-i][n-1-j]) ++onec;
                ans+=min(onec,4-onec);
            }
        }
        int tone=0;
        int tans=0;
        if(m&1){
            for(int i=0;i<n/2;++i){
                if(grid[m/2][i]!=grid[m/2][n-1-i]){
                    ++ans;
                    ++tans;
                }else if(grid[m/2][i]){
                    tone+=2;
                }
            }
        }

        if(n&1){
            for(int i=0;i<m/2;++i){
                if(grid[i][n/2]!=grid[m-1-i][n/2]){
                    ++ans;
                    ++tans;
                }else if(grid[i][n/2]){
                    tone+=2;
                }
            }
        }
        if((m&1) and (n&1)){
            if(grid[m/2][n/2]) ++ans;
        }
        if(tans==0)
            ans+=(tone%4);
        return ans;
    }
};
