class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int a = 0, m = g.size(), n = g[0].size(), o=0;
        for(int i = 0; i < m/2; ++i){
            for(int j = 0; j < n/2; ++j){
                int x = g[i][j] + g[i][n-1-j] + g[m-1-i][j] + g[m-1-i][n-1-j];
                if(x == 2){
                    a += 2;
                }else{
                    a += x&1;
                }
            }
        }
        if((m&1) && (n&1)){
            a += g[m/2][n/2];
        }
        bool fm = false, fn = false;
        if(n&1){
            for(int i = 0, j = n/2; i < m/2; ++i){
                if(g[i][j] != g[m-1-i][j]){
                    fm = true;
                    a++;
                }else if(g[i][j] == 1){
                    o += 2;
                }
            }
        }
        if(m&1){
            for(int i = m/2, j = 0; j < n/2; ++j){
                if(g[i][j] != g[i][n-1-j]){
                    fn = true;
                    a++;
                }else if(g[i][j] == 1){
                    o += 2;
                }
            }
        }
        //cout << a << " " << fm << " " << fn << endl;
        if(o % 4 == 2 && fm == false && fn == false){
            a += 2;
        }
        return a;
    }
};
