class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=0;
        int cnt=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt0=0,cnt1=0;
                if(grid[i][j]==0)cnt0++;
                else cnt1++;
                if(grid[n-i-1][j]==0)cnt0++;
                else cnt1++;
                if(grid[i][m-j-1]==0)cnt0++;
                else cnt1++;
                if(grid[n-i-1][m-j-1]==0)cnt0++;
                else cnt1++;
                if(cnt0>cnt1)ans+=cnt1;
                else {
                    ans+=cnt0;
                }
            }
            // cout<<ans<<" "<<cnt<<endl;
        }
        int flag1=0,flag2=0;
        if(n%2){
            int f=0,x=0;
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1])f++;
                else if(grid[n/2][j]==1)x+=2;
            }
            if(x%4){
               flag1++;
            }
            if(f)flag2++;
            ans+=f;
        }
          if(m%2){
            int f=0,x=0;
            for(int j=0;j<n/2;j++){
                if(grid[j][m/2]!=grid[n-j-1][m/2])f++;
                else if(grid[j][m/2]==1)x+=2;
            }
            if(x%4){
                flag1++;
            }
            if(f)flag2++;

            ans+=f;
        }

        if(flag1==1 and flag2==0 )ans+=2;
        if(n%2 and m%2 and grid[n/2][m/2]==1)ans++;

        return ans;

    }
};
