class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int count = 0;
        for (int r = 0; r < (grid.size()) / 2; r++) {
            for (int c = 0; c < (grid[0].size()) / 2; c++) {
                int num_one = 0;
                int num_zero = 0;
                num_one += grid[r][c];
                num_one += grid[grid.size() - 1 - r][c];
                num_one += grid[r][grid[0].size() - 1 - c];
                num_one += grid[grid.size() - 1 - r][grid[0].size() - 1 - c];
                num_zero = 4 - num_one;
                int factor = 1;
                if (r == grid.size() - 1 - r) factor *= 2;
                if (c == grid[0].size() - 1 - c) factor *= 2;
                // cout << r << " " << c << " " << num_one << " " << num_zero << " " << factor << endl;
                count += min(num_one, num_zero) / factor;
            }
        }
        vector<int> num_rem(3, 0);
        if (grid.size() % 2 == 1 && grid[0].size() % 2 == 1) {
            count += grid[(grid.size() - 1) / 2][(grid[0].size() - 1) / 2];
        }
        if (grid.size() % 2 == 1) {
            int mid = (grid.size()) / 2;
            for (int c = 0; c < (grid[0].size()) / 2; c++) {
                int idx = grid[mid][c] + grid[mid][grid[0].size() - 1 - c];
                num_rem[idx]++;
            }
        }
        if (grid[0].size() % 2 == 1) {
            int mid = (grid[0].size()) / 2;
            for (int c = 0; c < (grid.size()) / 2; c++) {
                int idx = grid[c][mid] + grid[grid.size() - 1 - c][mid];
                num_rem[idx]++;
            }
        }
        // cout << num_rem[0] << " " << num_rem[1] << " " << num_rem[2] << endl;
        if (num_rem[2] % 2 == 0) count += num_rem[1];
        else {
            // cout << count << endl;

            if (num_rem[1] == 0) count += 2;
            else count += num_rem[1];
        }
        return count;
    }
};
