class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int rows = grid.size();
        int cols = grid[0].size();
        for(int i=0; i<grid.size()/2; i++){
            for(int j=0; j<grid[i].size()/2; j++){
                int ones = 0;
                if(grid[i][j] == 1){
                    ones++;
                }
                if(grid.size() > 1 && grid[rows-1-i][j] == 1){
                    ones++;
                }
                if(grid[i].size() > 1 && grid[i][cols-1-j] == 1){
                    ones++;
                }
                if(grid.size() > 1 && grid[i].size() > 1 && grid[rows-1-i][cols-1-j] == 1){
                    ones++;
                }
                ans += min(ones, 4-ones);
            }
        }
        // cout<<ans<<" ";
        if(rows%2 == 1 && cols%2 == 1){
            if(grid[rows/2][cols/2] == 1){
                ans++;
            }
        }
        // cout<<ans<<" ";
        int count = 0;
        int inv = 0;
        if(cols%2 == 1){
            for(int j=0; j<rows/2; j++){
                if(grid[j][cols/2] != grid[rows-1-j][cols/2]){
                    ans++;
                    inv++;
                }
                if(grid[j][cols/2] == 1 && grid[rows-1-j][cols/2] == 1){
                    count += 2;
                }
            }
        }
        // cout<<ans<<" ";
        if(rows%2 == 1){
            for(int j=0; j<cols/2; j++){
                if(grid[rows/2][j] != grid[rows/2][cols-1-j]){
                    ans++;
                    inv++;
                }
                if(grid[rows/2][j] == 1 && grid[rows/2][cols-1-j] == 1){
                    count += 2;
                }
            }
        }
        if(count%4 != 0 && inv == 0){
            ans += 2;
        }
        // cout<<ans<<endl;
        return ans;
    }
};
