class Solution {
public:
     int check(vector<vector<int>> &grid){
        int n=grid.size(),m=grid[0].size();
        int cnt=0;
        int ans=0;

        int k1=0,k2=n-1;

        while(k1<k2){
            vector<int> tmp1=grid[k1],tmp2=grid[k2];
            int i1=0,j1=m-1,i2=0,j2=m-1;
            while(i1<j1){
                int ones=0;
                if(tmp1[i1]==1) ones++;
                if(tmp1[j1]==1) ones++;
                if(tmp2[i2]==1) ones++;
                if(tmp2[j2]==1) ones++;
                i1++;i2++;
                j1--;j2--;
                ans+=min(ones,4-ones);
            }
            k1++;
            k2--;
        }
        cnt=0;
        int eq=0;
        if(n%2){
            int imp=(n/2);
            int i=0,j=m-1;
            while(i<j){
                if(grid[imp][i]!=grid[imp][j]){
                    ans++;
                    cnt++;
                }else{
                    if(grid[imp][i]==1) eq+=2;
                }
                i++;
                j--;
            }
        }
        if(m%2){
             int imp=(m/2);
            int i=0,j=n-1;
            while(i<j){
                if(grid[i][imp]!=grid[j][imp]){
                    ans++;
                    cnt++;
                }
                else{
                    if(grid[i][imp]==1) eq+=2;
                }
                i++;
                j--;
            }

        }

        int mid=0;
        if((n%2) &&(m%2)){
            if(grid[(n/2)][m/2]==1) mid=1;
        }
        if(mid==1) ans++;
        if((eq%4)==2){
            if(cnt==0) ans+=2;
        }

        // if(cnt==1) ans++;



        return ans;
    }
    int minFlips(vector<vector<int>>& grid) {
        // int ans=;
        int ans=check(grid);
        return ans;

    }
};
