class Solution {
public:

    vector<vector<int>> v,dp;
    int rec(int idx,int mod){
        if(idx==v.size())
        {
            if(mod==0)
                return 0;
            return 1e9;
        }
        if(dp[idx][mod]!=-1)
            return dp[idx][mod];
        int ans=rec(idx+1, (mod+v[idx][0])%4) + v[idx][0];
        ans = min( ans, rec(idx+1, (mod-v[idx][1]+4)%4)+v[idx][1]);
        return dp[idx][mod]=ans;
    }
    int minFlips(vector<vector<int>>& g) {
        int n=g.size(),m=g[0].size();
        int ans=0;
        v.clear();
        for(int i=0;i<=(n-1)/2;i++){
            for(int j=0;j<=(m-1)/2;j++){
                vector<int> temp(2);
                temp[g[i][j]]++;
                if(i!=n-1-i)
                    temp[g[n-1-i][j]]++;
                if(j!=m-1-j)
                    temp[g[i][m-1-j]]++;
                if(i!=n-1-i && j!=m-1-j)
                    temp[g[n-1-i][m-1-j]]++;
                v.push_back(temp);
            }
        }
        int val=0;
        for(auto &x : g){
            for(auto &y : x)
                val+=y;
        }
        dp=vector<vector<int>> (v.size(),vector<int> (4,-1));
        return rec(0,val%4);
    }
};
