class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int number_of_ones = 0;
        int number_extra_pairs = 0;
        int changed_pair = 0;
        int ans = 0;
        // cout << (n+1)/2 << endl;
        int i = 0;
        while(i<=n-i-1){
            int lo = 0 , hi = m-1;
            // cout << i <<" " << n-i-1<< endl;
            while(lo<=hi){
                if(i == n-1-i){
                    // cout << sum << endl;
                    if(lo != hi){
                        int sum = grid[i][lo] + grid[i][hi];

//                         cout << sum << endl;

//                         cout <<"{ " << i <<  ", " << lo << " }" << endl;
//                         cout <<"{ " << i <<  ", " << hi << " }" << endl;
//                         cout <<"{ " << n-i-1 << ", " << lo << " }" << endl;
//                         cout <<"{ " << n-i-1 << ", " << hi << " }" << endl;
                        if(sum == 1){
                            ans ++;
                            number_of_ones += 2;
                            changed_pair ++;
                        }else if(sum == 2){
                            number_of_ones += 2;
                            number_extra_pairs ++;
                        }
                    }else{

                        if(grid[i][lo] == 1){
                            ans ++ ;
                        }
                    }   
                }
                else if(lo == hi){
                    int sum = grid[i][lo] + grid[n-i-1][hi];
                    if(sum == 1){
                        ans ++;
                        number_of_ones += 2;
                        changed_pair ++;
                    }else if(sum == 2){
                        number_of_ones += 2;
                        number_extra_pairs ++ ;
                    }
                }
                else{
                    int sum = grid[i][lo] + grid[i][hi] + grid[n-1-i][lo] + grid[n-1-i][hi];
                    if(sum == 1){
                        ans ++;
                    }else if(sum == 2){
                        ans += 2; 
                        number_of_ones += 4;
                    }else if(sum == 3){
                        ans += 1;
                        number_of_ones += 4;
                    }else if(sum == 4){
                        number_of_ones += 4;
                    }
                }

                lo ++ , hi -- ;
            }
            i++;
        }

        if(number_of_ones%4 != 0){
            if(changed_pair == 0){
                ans += 2;
            }
        }
        return ans;


    }
};
