class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        pair<int,int> p;
        int n=grid.size(),m=grid[0].size();
        int ans=0,count=0,c=0;
        vector<vector<int>> vec(n,vector<int> (m,0));
        for (int i=0;i<=n/2;i++){
            for (int j=0;j<=m/2;j++){
                set <pair<int,int>> st;
                if (vec[i][j]==1){
                    // cout<<"YES";
                    continue;
                }
                vec[i][j]=1,vec[i][m-j-1]=1,vec[n-i-1][m-j-1]=1,vec[n-i-1][j]=1;
                st.insert(make_pair(i,j)),st.insert(make_pair(i,m-j-1)),st.insert(make_pair(n-i-1,m-j-1)),st.insert(make_pair(n-i-1,j));
                if (st.size()==1){
                    if (grid[i][j]==1)ans++;
                }
                else if(st.size()==2){
                    // cout<<"yes";
                    if (grid[i][j]!=grid[i][m-j-1])ans++,c++;
                    if (grid[i][j]!=grid[n-i-1][j])ans++,c++;
                    if (j==m-j-1){
                        if (grid[i][j]==grid[n-i-1][j] && grid[i][j]==1)count++;
                    }
                    if (i==n-i-1){
                        if (grid[i][j]==grid[i][m-j-1] && grid[i][j]==1)count++;
                    }
                }
                else if(st.size()==4){
                    int val=0;
                    if (grid[i][j]==1)val++;
                    if (grid[i][m-j-1]==1)val++;
                    if (grid[n-i-1][m-j-1]==1)val++;
                    if (grid[n-i-1][j]==1)val++;
                    if (val==2)ans+=2;
                    else if (val==3 || val==1)ans++;
                }
            }
        }
        cout<<count<<" "<<c<<endl;
        if (count%2==0)return ans;
        else {
            if (c>0)return ans;
            else return ans+2;
        }
    }
};
