class Solution:


    def minFlips(self, grid: List[List[int]]) -> int:


        res = 0

        h, w = len(grid), len(grid[0])

        for i in range(h//2):
            for j in range(w//2):
                a1, b1 = grid[i][j], grid[i][w - 1 - j]
                c1, d1 = grid[h - 1 - i][j], grid[h - 1 - i][w - 1 - j]
                z0 = 0
                if a1 == 0:
                    z0 += 1
                if b1 == 0:
                    z0 += 1
                if c1 == 0:
                    z0 += 1
                if d1 == 0:
                    z0 += 1
                res += min(z0, 4 - z0)

        n2 = 0
        fh = 0
        if w % 2 == 1:
            for i in range(h//2):
                a, b = grid[i][w//2], grid[h - 1 - i][w//2]
                z0 = 0
                if a == 0:
                    z0 += 1
                if b == 0:
                    z0 += 1

                if z0 == 0:
                    n2 += 2
                elif z0 == 1:
                    res += 1
                    fh += 1

        if h % 2 == 1:
            for i in range(w//2):
                a, b = grid[h//2][i], grid[h//2][w - 1 - i]
                z0 = 0
                if a == 0:
                    z0 += 1
                if b == 0:
                    z0 += 1

                if z0 == 0:
                    n2 += 2
                elif z0 == 1:
                    res += 1
                    fh += 1

        if w % 2 == 1 and h % 2 == 1:
            z0 = 0
            if grid[h//2][w//2] == 0:
                z0 += 1
            if z0 == 0:
                res += 1

        if n2 % 4 == 2:
            if fh > 0:
                pass
            else:
                res += 2

        return res
