class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        pairs=0
        mm=m//2
        nn=n//2
        sum0=0
        res = 1 if m&1 and n&1 and grid[mm][nn] else 0
        if m&1:
            for i in range(nn):
                if grid[mm][i]!=grid[mm][-i-1]:
                    pairs+=1
                elif grid[mm][i]:
                    sum0+=1
        if n&1:
            for i in range(mm):
                if grid[i][nn]!=grid[-1-i][nn]:
                    pairs+=1
                elif grid[i][nn]:
                    sum0+=1
        res+=pairs
        if n&1 or m&1: 
            if sum0&1 and not pairs: res+=2
        for i in range(mm):
            for j in range(nn):
                s=grid[i][j]+grid[i][-j-1]+grid[-i-1][j]+grid[-i-1][-j-1]
                res+=min(s,4-s)
        return res
