class Solution
{
public:
    int minFlips(vector<vector<int>> &mat)
    {
        int n = mat.size(), m = mat[0].size(), result = 0;
        for (int r = 0; r < n / 2; r++)
            for (int c = 0; c < m / 2; c++)
            {
                int i = n - 1 - r, j = m - 1 - c;
                int x = mat[r][c] + mat[i][c] + mat[r][j] + mat[i][j];
                result += min(x, 4 - x);
            }
        if (n % 2 == 0 && m % 2 == 0)
            return result;

        if (n % 2 == m % 2)
            result += mat[n / 2][m / 2];

        int cnt = 0, dup = 0;
        if (n % 2 == 1)
            for (int c = 0; c < m / 2; c++)
            {
                int cc = m - 1 - c;
                if (mat[n / 2][c] != mat[n / 2][cc])
                    cnt++;
                if (mat[n / 2][c] + mat[n / 2][cc] == 2)
                    dup++;
            }
        if (m % 2 == 1)
            for (int r = 0; r < n / 2; r++)
            {
                int rr = n - 1 - r;
                if (mat[r][m / 2] != mat[rr][m / 2])
                    cnt++;
                if (mat[r][m / 2] + mat[rr][m / 2] == 2)
                    dup++;
            }
        if ((dup & 1) && !cnt)
            result += 2;
        return result + cnt;
    }
};
