class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        ans = 0

        if n % 2 == 1:
            if m % 2 == 1:
                if grid[n//2][m//2] == 1:
                    ans += 1
                    grid[n//2][m//2] == 0

        r, c = 0, 0
        rc, cc = 0, 0
        if n % 2 == 1:
            i = n//2
            for j in range(m//2):
                r += grid[i][j] + grid[i][m-1-j]
                rc += abs(grid[i][j] - grid[i][m-1-j])
        if m % 2 == 1:
            j = m//2
            for i in range(n//2):
                c += grid[i][j] + grid[n-1-i][j]
                cc += abs(grid[i][j] - grid[n-1-i][j])

        for i in range(n):
            for j in range(m):
                if grid[i][j] != grid[i][m-1-j]:
                    if grid[i][j] != grid[n-1-i][j]:
                        grid[i][j] = 1 - grid[i][j]
                    else:
                        grid[i][m-1-j] = 1 - grid[i][m-1-j]
                    ans += 1
                else:
                    if grid[i][j] != grid[n-1-i][j]:
                        grid[n-1-i][j] = 1 - grid[n-1-i][j]
                        ans += 1

        if (r+c) % 4 > 0:
            if (rc+cc) == 0:
                ans += 2

        return ans
