class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int nby2=(n/2)+(n&1),mby2=(m/2)+(m&1),ans=0,zerozero=0,onezero=0,oneone=0;
        for(int i=0;i<nby2;i++){
            for(int j=0;j<mby2;j++){
                set<pair<int,int>>cell;
                cell.insert({i,j});
                cell.insert({n-1-i,j});
                cell.insert({i,m-1-j});
                cell.insert({n-1-i,m-1-j});
                if(cell.size()==4){
                    int cntzero=0,cntone=0;
                    for(auto it:cell){
                        if(grid[it.first][it.second])   cntone++;
                        else    cntzero++;
                    }
                    ans+=min(cntone,cntzero);
                }
                else if(cell.size()==2){
                    int cntzero=0,cntone=0;
                    for(auto it:cell){
                        if(grid[it.first][it.second])   cntone++;
                        else    cntzero++;
                    }
                    if(cntzero && cntone)   onezero++;
                    else if(cntzero)    zerozero++;
                    else    oneone++;  
                }
            }
        }
        if(oneone%2){
            if(onezero){
                ans++;
                onezero--;
            } 
            else    ans+=2;
        }
        ans+=onezero;
        if(n&1 && m&1 && grid[nby2-1][mby2-1])    ans++;
        return ans;
    }
};
