class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
           for(int j=0;j<m/2;j++){
              vector<int> cnt(2,0);
              cnt[grid[i][j]]++;
              cnt[grid[n-1-i][j]]++;
              cnt[grid[i][m-1-j]]++;
              cnt[grid[n-1-i][m-1-j]]++;
              ans+=(min(cnt[0],cnt[1]));
           }
        }
        map<pair<int,int>,int> mp;
        if(m&1){
          for(int i=0;i<n/2;i++){
            if(grid[i][m/2]!=grid[n-1-i][m/2]){
              mp[{0,1}]++;
            }
            else{
              mp[{grid[i][m/2],grid[i][m/2]}]++;
            }
          }
        }
        if(n&1){
          for(int j=0;j<m/2;j++){
            if(grid[n/2][j]!=grid[n/2][m-1-j]){
              mp[{0,1}]++;
            }
            else{
              mp[{grid[n/2][j],grid[n/2][j]}]++;
            }
          }
        }
        if(mp[{1,1}]%2!=0){
          if(mp[{0,1}]>0){
            ans+=mp[{0,1}];
          }
          else{
            ans+=2;
          }
        }
        else{
          ans+=mp[{0,1}];
        }
        if(m&1 and n&1){
          ans+=(grid[n/2][m/2]==1);
        }
        return ans;

    }
};
