class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        index4,index2,index1=0,0,0
        result=0
        r4,r2,r1=0,0,0
        cur1=0
        for i in range((m+1)//2):
            for j in range((n+1)//2):
                newi=m-1-i
                newj=n-1-j
                ls=[(i,j),(newi,j),(i,newj),(newi,newj)]
                set1=set(ls)
                cnt=[0,0]

                for a,b in set1:
                    cnt[grid[a][b]]+=1
                curlen=len(set1)
                if curlen==4:
                    r4+=min(cnt[0],cnt[1])
                if curlen==2:
                    index2+=1

                    r2+=min(cnt[0],cnt[1])
                    if cnt[0]!=2:
                        cur1+=2
                if curlen==1:
                    index1+=1
                    r1+=min(cnt[0],cnt[1])
                    cur1+=cnt[1]
        if cur1%4==0:
            return r1+r4+r2
        m=cur1%4
        if m==1:
            return r4+r2+1
        if m==2:
            if r2==0:
                le=2
            else:
                le=0
            return r4+r2+le
        if m==3:
            if r2==0:
                le=2
            else:
                le=0
            return r4+r2+le+1
