class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        vector<pair<int, int>> m1; int m2 = 0, m3 = 0, m20  = 0, m21 = 0;
        int m = a.size(), n = a[0].size(), ans = 0;
        for (int i = 0, j = m-1; i <= j; i++, j--) {
            for (int st = 0, ed = n-1; st <= ed; st++, ed--) {
                if (i == j && st == ed) { ans += a[i][st] == 1; }
                else if (i != j && st != ed) {
                    int n0 = 0, n1 = 0;
                    n0 += a[i][st] == 0;
                    n0 += a[i][ed] == 0;
                    n0 += a[j][st] == 0;
                    n0 += a[j][ed] == 0;
                    n1 += a[i][st] == 1;
                    n1 += a[i][ed] == 1;
                    n1 += a[j][st] == 1;
                    n1 += a[j][ed] == 1;
                    m1.emplace_back(n0, n1);
                } else if (i == j) {
                    m2 += a[i][st] == a[i][ed];
                    if (a[i][st] == a[i][ed] && a[i][st] == 0) m20++;
                    else m21++;
                    m3 += a[i][st] != a[i][ed];
                } else {
                    m2 += a[i][st] == a[j][st];
                    if (a[i][st] == a[j][st] && a[i][st] == 0) m20++;
                    else m21++;
                    m3 += a[i][st] != a[j][st];
                }
            }
        }
        for (auto &v : m1) {
            ans += min(v.first, v.second);
        }
        if (m3 > 0) {
            ans += m3;
        } else if (m21 % 2 != 0) {
            ans += 2;
        }        
        return ans;
    }
};
