class Solution {
public:
    int n,m;
    int minFlips(vector<vector<int>>& a) {
        n=a.size();
        m=a[0].size();
        int ans=0;
        int twoone=0;
        bool onetwo=0;
        for(int i=0;i<((n+1)/2);i++)
        {
            for(int j=0;j<((m+1)/2);j++)
            {
                set<pair<int,int>> st;
                st.insert({i,j});
                st.insert({(n-1)-i,j});
                st.insert({i,(m-1)-j});
                st.insert({(n-1)-i,(m-1)-j});
                int one=0;
                for(auto x:st)
                {
                    if(a[x.first][x.second]==1)
                        one++;
                }
                // cout<<i<<" ij "<<j<<endl;
                // cout<<st.size()<<" "<<one<<endl;
                if(st.size()==4)
                {
                    ans+=min(4-one,one);
                }
                else if(st.size()==2)
                {
                    if(one==2)
                    {
                        twoone++;
                    }
                    else if(one==1)
                    {
                        onetwo=1;
                        ans++;
                    }

                }
                else if(st.size()==1)
                {
                    if(one==1)
                        ans++;
                }
                else
                {
                    return -1;
                }
            }
        }
        if(!onetwo)
            ans+=(twoone%2)*2;
        return ans;
    }
};
