class Solution {
public:
    int minFlips(vector<vector<int>>& s) {
        int ans = 0;
        int n = s.size(), m = s[0].size();
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int cnt[2];cnt[0]=cnt[1] = 0;
                cnt[s[i][j]]++;
                cnt[s[n-i-1][j]]++;
                cnt[s[n-i-1][m-j-1]]++;
                cnt[s[i][m-j-1]]++;
                ans += min(cnt[0], cnt[1]);
            }
        }
        int nott = 0;
        int o = 0;
        if(n&1){   
            for(int j=0; j<m/2; j++){
                int k = int(s[n/2][j] != s[n/2][m-j-1]);
                ans += k;
                nott += k;
                if(s[n/2][j] == 1)o++;
                if(s[n/2][m-j-1])o++;
            }
        }
        if(m&1){
            for(int i=0; i<n/2; i++){
                int k = int(s[i][m/2] != s[n-i-1][m/2]);
                ans += k;
                nott+=k;
                if(s[i][m/2] == 1)o++;
                if(s[n-i-1][m/2] == 1)o++;
            }

        }
        if((o-nott)%4 and (o+nott)%4){
            if(nott < 2) ans += 2;
        }
        if(n&1 and m&1 and s[n/2][m/2]==1){
            ans++;
        }
        return ans;
    }
};
