class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        for (int i = 0; i < (m / 2); i++){
            for (int j = 0; j < (n / 2); j++){
                int a = 0,b = 0;
                if (grid[i][j] == 0)
                    a ++;
                else 
                    b ++;
                if (grid[i][n - j - 1] == 0)
                    a ++;
                else 
                    b ++;
                if (grid[m - i - 1][j] == 0)
                    a ++;
                else 
                    b ++;
                if (grid[m - i - 1][n - j - 1] == 0)
                    a ++;
                else 
                    b ++;
                ans += min(a,b);
                cout<<ans<<endl;
            }
        }
        if (m % 2 == 1 && n % 2 == 1){
            int a = 0,b = 0;
            for (int i = 0; i < (m / 2); i++){
                if (grid[i][n/2] != grid[m - i - 1][n/2])
                    a ++;
                else if(grid[i][n/2] == 1)
                    b ++;
            }
            for (int j = 0; j < (n / 2); j++){
                if (grid[m/2][j] != grid[m/2][n - j - 1])
                    a ++;
                else if(grid[m/2][j] == 1)
                    b ++;
            }
            if(grid[m/2][n/2] == 1)
                ans ++;
            ans += a;
            if (a == 0 && b % 2 == 1)
                ans += 2;
            cout<<ans<<endl;
        }
        else if(m % 2 == 1){
            int a = 0,b = 0;
            for (int j = 0; j < (n / 2); j++){
                if (grid[m/2][j] != grid[m/2][n - j - 1])
                    a ++;
                else if(grid[m/2][j] == 1)
                    b ++;
            }
            ans += a;
            if (a == 0 && b % 2 == 1)
                ans += 2;
            cout<<ans<<endl;
        }
        else if(n % 2 == 1){
            int a = 0,b = 0;
            for (int i = 0; i < (m / 2); i++){
                if (grid[i][n/2] != grid[m - i - 1][n/2])
                    a ++;
                else if(grid[i][n/2] == 1)
                    b ++;
            }
            ans += a;
            if (a == 0 && b % 2 == 1)
                ans += 2;
            cout<<ans<<endl;
        }
        return ans;
    }
};
