class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int flip=0;
        for(int i=0;i<n/2;++i){
            for(int j=0;j<m/2;++j){
                int cnt=0;
                if(grid[i][j]==1)cnt++;
                if(grid[i][m-j-1]==1)cnt++;
                if(grid[n-i-1][j]==1)cnt++;
                if(grid[n-i-1][m-j-1]==1)cnt++;
                if(cnt==1 || cnt==3)flip++;
                else if(cnt==2)flip+=2;
            }
        }
        bool flag=0;
        int one=0;
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1)flip++;
        }
        if(n%2==1){
            for(int j=0;j<m/2;++j){
                if(grid[n/2][j]==1 && grid[n/2][m-j-1]==1)one+=2;
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;++i){
                if(grid[i][m/2]==1 && grid[n-i-1][m/2])one+=2;
            }
        }
        if(one%4==2)flag=1;
        if(n%2==1){
            for(int j=0;j<m/2;++j){
                if((grid[n/2][j]==1 && grid[n/2][m-j-1]==0) ||
                    (grid[n/2][j]==0 && grid[n/2][m-j-1]==1)){
                        if(flag){
                            flag=0;
                        }
                        flip++;
                    }
            }
        }
        if(m%2==1){
            for(int i=0;i<n/2;++i){
                if((grid[i][m/2]==1 && grid[n-i-1][m/2]==0 )||
                (grid[i][m/2]==0 && grid[n-i-1][m/2]==1)){
                    if(flag)flag=0;
                    flip++;
                }
            }
        }
        if(flag)flip+=2;
        return flip;
    }
};
