class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size(), m = grid[0].size();
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                // (i, j)  (i, m - 1 - j)  (n - 1 - i, j) (n - 1 - i, m - 1 - j)
                vector<int> v(2, 0);
                v[grid[i][j]]++; v[grid[i][m - 1 - j]]++; 
                v[grid[n - 1 - i][j]]++; v[grid[n - 1 - i][m - 1 - j]]++;
                ans += min(v[0], v[1]);
            }
        }
        if((n & 1) and (m & 1)){
            int ones = 0, not_ = 0;
            for(int j = 0; j < m / 2; j++){
                if(grid[n / 2][m - 1 - j] == grid[n / 2][j] and grid[n / 2][j] == 1) ones++;
                if(grid[n / 2][j] != grid[n / 2][m - 1 - j]) not_++;
            }
            for(int i = 0; i < n / 2; i++){
                if(grid[i][m / 2] == grid[n - 1 - i][m / 2] and grid[i][m / 2] == 1) ones++;
                if(grid[i][m / 2] != grid[n - 1 - i][m / 2]) not_++;
            }
            if(ones & 1){
                if(not_ > 0) ans += not_;
                else ans += 2;
            }else{
                ans += not_;
            }
            ans += grid[n / 2][m / 2];
        }
        else if(n & 1){
            int ones = 0, not_ = 0;
            for(int j = 0; j < m / 2; j++){
                if(grid[n / 2][m - 1 - j] == grid[n / 2][j] and grid[n / 2][j] == 1) ones++;
                if(grid[n / 2][j] != grid[n / 2][m - 1 - j]) not_++;
            }
            if(ones & 1){
                if(not_ > 0) ans += not_;
                else ans += 2;
            }else{
                ans += not_;
            }
        }
        else if(m & 1){
            int ones = 0, not_ = 0;
            for(int i = 0; i < n / 2; i++){
                if(grid[i][m / 2] == grid[n - 1 - i][m / 2] and grid[i][m / 2] == 1) ones++;
                if(grid[i][m / 2] != grid[n - 1 - i][m / 2]) not_++;
            }
            if(ones & 1){
                if(not_ > 0) ans += not_;
                else ans += 2;
            }else{
                ans += not_;
            }
        }
        return ans;
    }
};
