class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size(), cols = grid[0].size();
        int count1 = 0, count2 = 0, count3 = 0, count4 = 0;
        int x = 0;
        for(int i=0;i<=rows-i-1;i++) {
            for(int j=0;j<=cols-j-1;j++){
                if(i==rows-i-1 && j==cols-j-1) {
                    if(grid[i][j] == 1) count1++;
                } else if(i==rows-1-i) {
                    if(grid[i][j]!=grid[i][cols-j-1]) {
                        count2++;
                    }
                    if(grid[i][j] == 1 && grid[i][cols-j-1]==1) x+=2;
                } else if(j==cols-j-1) {
                    if(grid[i][j]!=grid[rows-i-1][j]) {
                        count3++;
                    }
                    if(grid[i][j] == 1 && grid[rows-i-1][j]==1) x+=2;
                } else {
                    int ones = 0, zeros = 0;
                    if(grid[i][j] == 0) zeros++;
                    else ones++;
                    if(grid[i][cols-j-1] == 0) zeros++;
                    else ones++;
                    if(grid[rows-i-1][j] == 0) zeros++;
                    else ones++;
                    if(grid[rows-i-1][cols-j-1] == 0) zeros++;
                    else ones++;
                    if(ones == 2)  count4+=2;
                    if(ones == 1 || ones == 3) count4++; 
                }
            }
        }
        if(x%4 && !count2 && !count3) {
            return count1+count4+(x%4);
        }
        return count1+count2+count3+count4;
    }
};
