class Solution {
public:
    int minFlips(vector<vector<int>>& a) 
    {
        int n = a.size(),m = a[0].size(),i,j,cnt,sum,ans=0;
        unordered_map<int,vector<int>> mp;
        vector<vector<int>> vis(n,vector<int>(m));
        for(i=0;i<n;i++)
        {
            for(j=0;j<m;j++)
            {
                if(!vis[i][j])
                {
                    cnt = 1;
                    sum = a[i][j];
                    vis[i][j] = 1;
                    if(!vis[n-i-1][j])
                    {
                        vis[n-i-1][j] = 1;
                        sum+=a[n-i-1][j];
                        cnt++;
                    }
                    if(!vis[i][m-j-1])
                    {
                        vis[i][m-j-1] = 1;
                        sum+=a[i][m-j-1];
                        cnt++;
                    }
                    if(!vis[n-i-1][m-j-1])
                    {
                        vis[n-i-1][m-j-1] = 1;
                        sum+=a[n-i-1][m-j-1];
                        cnt++;
                    }
                    mp[cnt].push_back(sum);
                }
            }
        }
        for(auto x : mp[4])
        {
            ans+=min(x,4-x);
        }
        if(mp[1].size() > 0 && mp[1][0] > 0)
        {
            ans++;
        }
        cnt = 0;
        sum = 0;
        for(auto x : mp[2])
        {
            if(x == 1)
            {
                cnt++;
            }
            if(x == 2)
            {
                sum++;
            }
        }
        auto one = (cnt + 2*sum) % 4;
        if(sum%2 == 0 or cnt > 0)
        {
            return ans + cnt;
        }
        return ans + cnt + 2;
    }
};
