class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int m=a.size();
        int n=a[0].size();
        int ans=0;
        if(m%2==0 && n%2==0)
        {
            for(int i=0;i<m/2;i++)
            {
                for(int j=0;j<n/2;j++)
                {
                    int v1=a[i][j];
                    int v2=a[i][n-j-1];
                    int v3=a[m-i-1][j];
                    int v4=a[m-i-1][n-j-1];
                    ans+=min(v1+v2+v3+v4,4-(v1+v2+v3+v4));
                }
            }
        }
        else if(n%2 && m%2==0)
        {
            for(int i=0;i<m/2;i++)
            {
                for(int j=0;j<n/2;j++)
                {
                    int v1=a[i][j];
                    int v2=a[i][n-j-1];
                    int v3=a[m-i-1][j];
                    int v4=a[m-i-1][n-j-1];
                    ans+=min(v1+v2+v3+v4,4-(v1+v2+v3+v4));
                }
            }
            int o=0, bad=0;
            for(int i=0;i<m/2;i++)
            {
                ans+=(a[i][n/2]!=a[m-i-1][n/2]);
                o+=a[i][n/2];
                o+=a[m-i-1][n/2];
                if(a[i][n/2]!=a[m-i-1][n/2])
                    bad=1;
            }
            if(!bad && o%4)
                ans+=2;
        }
        else if(m%2 && n%2==0)
        {
            for(int i=0;i<m/2;i++)
            {
                for(int j=0;j<n/2;j++)
                {
                    int v1=a[i][j];
                    int v2=a[i][n-j-1];
                    int v3=a[m-i-1][j];
                    int v4=a[m-i-1][n-j-1];
                    ans+=min(v1+v2+v3+v4,4-(v1+v2+v3+v4));
                }
            }
            int o=0, bad=0;
            for(int j=0;j<n/2;j++)
            {
                ans+=(a[m/2][j]!=a[m/2][n-j-1]);
                o+=a[m/2][j];
                o+=a[m/2][n-j-1];
                if(a[m/2][j]!=a[m/2][n-j-1])
                    bad=1;
            }   
            if(!bad && o%4)
                ans+=2;
        }
        else
        {
            for(int i=0;i<m/2;i++)
            {
                for(int j=0;j<n/2;j++)
                {
                    int v1=a[i][j];
                    int v2=a[i][n-j-1];
                    int v3=a[m-i-1][j];
                    int v4=a[m-i-1][n-j-1];
                    ans+=min(v1+v2+v3+v4,4-(v1+v2+v3+v4));
                }
            }
            ans+=a[m/2][n/2];
            int o=0, bad=0;
            for(int i=0;i<m/2;i++)
            {
                ans+=(a[i][n/2]!=a[m-i-1][n/2]);
                o+=a[i][n/2];
                o+=a[m-i-1][n/2];
                if(a[i][n/2]!=a[m-i-1][n/2])
                    bad=1;
            }
            for(int j=0;j<n/2;j++)
            {
                ans+=(a[m/2][j]!=a[m/2][n-j-1]);
                o+=a[m/2][j];
                o+=a[m/2][n-j-1];
                if(a[m/2][j]!=a[m/2][n-j-1])
                    bad=1;
            }   
            if(!bad && o%4)
                ans+=2;
        }
        return ans;
    }
};
