class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        for( int i=0; i<n/2; i++ ){
            for( int j=0; j<m/2; j++ ){
                int one_cnt = 0;
                if( grid[i][j] == 1 )
                    one_cnt++;
                if( grid[i][m-1-j] == 1 )
                    one_cnt++;
                if( grid[n-1-i][j] == 1 )
                    one_cnt++;
                if( grid[n-1-i][m-1-j] == 1 )
                    one_cnt++;

                int val = max( one_cnt, 4-one_cnt );
                ans += (4-val);
            }
        }


        if( (n%2 == 1) && (m%2 == 1) ){
            if( grid[(n-1)/2][(m-1)/2] == 1 )
                ans++;
        }

        int pos = 0;
        int one_cnt = 0;
        if( n%2 == 1 ){
            for( int i=0; i<m/2; i++ ){
                if( grid[(n-1)/2][i] != grid[(n-1)/2][m-1-i] )
                    pos++;
                if( grid[(n-1)/2][i] == 1 )
                    one_cnt++;
                if( grid[(n-1)/2][m-1-i] == 1 )
                    one_cnt++;
            }
        }

        if( m%2 == 1 ){
            for( int i=0; i<n/2; i++ ){
                if( grid[i][(m-1)/2] != grid[n-1-i][(m-1)/2] )
                    pos++;
                if( grid[i][(m-1)/2] == 1 )
                    one_cnt++;
                if( grid[n-1-i][(m-1)/2] == 1 )
                    one_cnt++;
            }
        }

        int val = max( min(one_cnt%4, (4-(one_cnt)%4)%4), pos);
        ans += val;
        return ans;
    }
};
