class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                p = grid[i][j] + grid[m-1-i][n-1-j] + grid[i][n-1-j] + grid[m-1-i][j]
                # print(i, j, p)
                ans += min(p, 4-p)
        s = p = 0
        if m % 2 == 1 and n % 2 == 1:
            for i in range(m // 2):
                if grid[i][n//2] != grid[m-1-i][n//2]:
                    p += 1
                else:
                    s += grid[i][n//2] + grid[m-1-i][n//2]
            for j in range(n // 2):
                if grid[m//2][j] != grid[m//2][n-1-j]:
                    p += 1
                else:
                    s += grid[m//2][j] + grid[m//2][n-1-j]
            if grid[m//2][n//2] == 1:
                ans += 1
        elif m % 2 == 1 and n % 2 == 0:
            for j in range(n // 2):
                if grid[m//2][j] != grid[m//2][n-1-j]:
                    p += 1
                else:
                    s += grid[m//2][j] + grid[m//2][n-1-j]
        elif m % 2 == 0 and n % 2 == 1:
            for i in range(m // 2):
                if grid[i][n//2] != grid[m-1-i][n//2]:
                    p += 1
                else:
                    s += grid[i][n//2] + grid[m-1-i][n//2]
        if p == 0 and s % 4 != 0:
            ans += 2
        ans += p
        return ans
