class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0 , n = grid.size() , m = grid[0].size() , cnt1 = 0 , cnt2 = 0 , total = 0 ;

        for( auto x : grid ){
            for( auto y : x ){
                total += y ;
            }
        }

        for( int i = 0 , j = n-1 ; i <= j ; i++ , j-- ){

            for( int k = 0 , l = m-1 ; k <= l ; k++ , l-- ){

                if( i != j && k != l ){
                    int val = grid[i][k] + grid[i][l] + grid[j][k] + grid[j][l] ;
                    ans += min( 4-val , val ) ;
                    total -= val ;
                }
                else if( i != j ){
                    if( grid[i][k] != grid[j][k] )  cnt1++ ;
                }
                else if( k != l ){
                    if( grid[i][k] != grid[i][l] )  cnt1++ ;
                }
                else{
                    cnt2++ ;
                }   
            }
        }

        total += cnt1 ;

        // cout << total << " " << ans << " " << cnt1 << " " << cnt2 << "\n" ;

        if( (total%4) == 0 ){
            return ans + cnt1 ;
        }
        else if( total%4 == 2 ){
            if( cnt1 == 0 ){
                ans+=2;
            }
            else{
                ans += cnt1 ;
            }
            return ans ;
        }
        else if( total%4 == 1 ){
            return ans + cnt1 + 1 ;
        }
        else{
            if( cnt1 == 0 ){
                ans += 3 ;
            }
            else{
                ans += cnt1 + 1 ;
            }
            return ans ;
        }
    }
};
