class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        // vector<int> sol;
        int ans = 0;
        int addi = 0;
        int r1 = 0;
        int r2 = n-1;
        while(r1<r2){
            int c1 = 0;
            int c2 = m-1;
            while(c1<c2){
                int cnt = 0;
                if (grid[r1][c1]==1) cnt++;
                if (grid[r1][c2]==1) cnt++;
                if (grid[r2][c1]==1) cnt++;
                if (grid[r2][c2]==1) cnt++;
                int k = min(cnt, 4-cnt);
                ans += k;
                c1++;
                c2--;
            }
            r1++;
            r2--;
        }
        int ones = 0;
        if (m%2!=0){
            int r1 = 0;
            int r2 = n-1;
            while(r1<r2){
                if (grid[r1][m/2]!=grid[r2][m/2]) addi++;
                if (grid[r1][m/2]==1 && grid[r2][m/2]==1) ones+=2;
                r1++;
                r2--;
            }
        }
        if (n%2!=0){
            int c1 = 0;
            int c2 = m-1;
            while(c1<c2){
                if (grid[n/2][c1]!=grid[n/2][c2]) addi++;
                if (grid[n/2][c1]==1 && grid[n/2][c2]==1) ones+=2;
                c1++;
                c2--;
            }
        }
        if (n%2!=0 && m%2!=0 && grid[n/2][m/2]==1){
            ans++;
        }
        int k = ones%4;
        if (k=2){
            if (addi>=1){
                ones = 0;
            }
        }
        return ans + addi + (ones%4);
    }
};
