class Solution:
    def minFlips(self, s: List[List[int]]) -> int:
        m,n = len(s),len(s[0])
        r = 0
        for i in range(m//2):
            for j in range(n//2):
                if(s[i][j] == s[i][n-j-1] == s[m-i-1][j] == s[m-i-1][n-j-1]):
                    continue
                else:
                    a = sum([s[i][j],s[i][n-j-1],s[m-i-1][j],s[m-1-i][n-1-j]])
                    a = min([a,4-a])
                    r += a
        row,col = m%2==1,n%2==1
        print(r)
        if(row or col):
            if(row and col):
                r += s[m//2][n//2]
            rs,cs = 0,0
            rn,cn = 0,0
            if(row):
                for i in range(n//2):
                    if(s[m//2][i] == s[m//2][n-i-1]):
                        rs += s[m//2][i] *2
                    else:
                        rn += 1
            if(col):
                for i in range(m//2):
                    if(s[i][n//2] == s[m-i-1][n//2]):
                        cs += s[i][n//2] *2
                    else:
                        cn += 1
            if((cs+rs)%4!=0 and (cn+rn) ==0):
                r += 2
            else:
                r += cn+rn
        return r
