class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        // cout << n << " " << m << endl;
        int ans = 0;
        for (int i = 0; i < n / 2; i++){
            for (int j = 0; j < m / 2; j++){
                int cnt1 = 0;
                if (grid[i][j] == 1) cnt1++;
                if (grid[i][m - 1 - j] == 1) cnt1++;
                if (grid[n - 1 - i][j] == 1) cnt1++;
                if (grid[n - 1 - i][m - 1 - j] == 1) cnt1++;
                ans += min(cnt1, 4 - cnt1);
            }
        }
        // cout << ans << endl;
        if (n % 2 && m % 2){
            ans += grid[n/2][m/2];
            int cnt = 0;
            for (int i = 0; i < n; i++){
                cnt += grid[i][m/2];
            }
            for (int j = 0; j < m; j++){
                cnt += grid[n/2][j];
            }
            cnt -= 2 * grid[n/2][m/2];
            cnt %= 4;
            int diff = 0;
            for (int i = 0; i < n/2; i++){
                if (grid[i][m/2] != grid[n - 1 - i][m/2]){
                    diff++;
                }
            }
            for (int j = 0; j < m/2; j++){
                if (grid[n/2][j] != grid[n/2][m - 1- j]){
                    diff++;
                }
            }
            cout << cnt << " " << diff << endl;
            ans += diff;
            if (cnt + diff < 4 && cnt - diff > 0){
                ans += min({cnt , 4 - diff - cnt});
            }
        }
        else if (n % 2){
            int cnt = 0;
            for (int j = 0; j < m; j++){
                cnt += grid[n/2][j];
            }
            cnt %= 4;
            int diff = 0;
            for (int j = 0; j < m/2; j++){
                if (grid[n/2][j] != grid[n/2][m - 1- j]){
                    diff++;
                }
            }
            cout << cnt << " " << diff << endl;
            ans += diff;
            if (cnt + diff < 4 && cnt - diff > 0){
                ans += min({cnt, 4 - diff - cnt});
            }
        }
        else if (m % 2){
            int cnt = 0;
            for (int i = 0; i < n; i++){
                cnt += grid[i][m/2];
            }
            cnt %= 4;
            int diff = 0;
            for (int i = 0; i < n / 2; i++){
                if (grid[i][m/2] != grid[n - 1 - i][m/2]){
                    diff++;
                }
            }
            cout << cnt << " " << diff << endl;
            ans += diff;
            if (cnt + diff < 4 && cnt - diff > 0){
                ans += min({cnt, 4 - diff - cnt});
            }
        }
        return ans;
    }
};
