class Solution(object):
    def minFlips(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """
        #for a certain cell (i,j), must be equal to cell (i,len(grid[0])-j-1)
        #and must be equal to (len(grid)-i-1,j) and must be equal to (len(grid)-i-1,len(grid[0])-j-1)
        mydict = {}
        for i in range (len(grid)):
            for j in range (len(grid[0])):
                c1 = (i,j)
                c2 = (i,len(grid[0])-j-1)
                c3 = (len(grid)-i-1,j)
                c4 = (len(grid)-i-1,len(grid[0])-j-1)
                cur = set([c1,c2,c3,c4])
                cur = tuple(sorted(list(cur)))
                if cur not in mydict:
                    cd = {}
                    for x in cur:
                        if grid[x[0]][x[1]] not in cd:
                            cd[grid[x[0]][x[1]]] = 1
                        else:
                            cd[grid[x[0]][x[1]]] += 1
                    mydict[cur] = cd
        #we can either make everything zero, 
        #a group can have 4 or 2 or 1 cell. all the size1 cells must be
        res = 0
        od = {}
        zero1 = 0
        have2 = 0
        double0 = 0
        #how many 2's can we have?
        for x in mydict:
            if len(x) == 4:
                if len(mydict[x]) == 2:
                    res+=min(mydict[x][0],mydict[x][1])
            elif len(x) == 2:
                # print (mydict[x],x)
                if 0 in mydict[x] and 1 in mydict[x]:
                    res+=1
                    zero1+=1
                elif 0 not in mydict[x]:
                    have2+=1
                else:
                    double0+=1
            else:
                # print (1, "H")
                # print ("HIHIHIH")
                if 1 in mydict[x]:
                    res+=1
        have2*=2
        # print (have2,res, zero1)
        if have2%4 != 0:
            res+=2
            if zero1 > 0:
                res-=2
        return res
