class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int zz=n/2+(n%2);
        int yy=m/2+(m%2);
        int ans=0;
        int co_one=0;
        int extra=0;
        for(int i=0;i<zz;i++)
        {

            for(int j=0;j<yy;j++)
            {
                // cout<<ans<<" "<<i<<" "<<j<<endl;
                if((i == (n-1-i)) && (j == (m-1-j)))
                {
                    if(grid[i][j])
                    co_one++;
                    continue;
                }
                if(i == (n-1-i))
                {
                    int x=grid[i][j];
                    int y=grid[i][m-1-j];
                    int zero=0,one=0;
                    if(x == 0) zero++;
                    if(y == 0) zero++;
                    one=2-zero;
                    ans+=min(one,zero);
                    if(one == 2) co_one+=2;
                    if(one == 1) extra=2;
                    continue;
                }
                if(j == (m-1-j))
                {
                    int x=grid[i][j];
                    int z=grid[n-1-i][j];
                    int zero=0,one=0;
                    if(x == 0) zero++;
                    if(z == 0) zero++;
                    one=2-zero;
                    ans+=min(one,zero);
                    if(one == 2) co_one+=2;
                    if(one == 1) extra=2;
                    continue;
                }
                int x=grid[i][j];
                int y=grid[i][m-1-j];
                int z=grid[n-1-i][j];
                int w=grid[n-1-i][m-1-j];
                int zero=0,one=0;
                if(x == 0) zero++;
                if(y == 0) zero++;
                if(z == 0) zero++;
                if(w == 0) zero++;
                one=4-zero;
                ans+=min(one,zero);
            }
        }
        // cout<<ans;
        // cout<<endl;
        return ans+min(co_one%4,(co_one+extra)%4);
    }
};
