class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int i, j, m = grid.size(), n = grid[0].size(), ans = 0, p1, p2, p3, p4, zo, oo;
        for(i=0;i<m/2;i++)    for(j=0;j<n/2;j++){
            p1 = grid[i][j];
            p2 = grid[i][n - j - 1];
            p3 = grid[m - i - 1][j];
            p4 = grid[m - i - 1][n - j - 1];
            ans += min(p1 + p2 + p3 + p4, 4 - (p1 + p2 + p3 + p4));
        }

        zo = 0; oo = 0;
        if(m % 2)
        for(j=0;j<n/2;j++){
            p1 = grid[m / 2][j];
            p2 = grid[m / 2][n - j - 1];
            ans += min(p1 + p2, 2 - (p1 + p2));
            if(p1 + p2 == 1)    zo++;
            if(p1 + p2 == 2)    oo++;
        }

        if(n % 2)
        for(i=0;i<m/2;i++){
            p1 = grid[i][n / 2];
            p2 = grid[m - i - 1][n / 2];
            ans += min(p1 + p2, 2 - (p1 + p2));
            if(p1 + p2 == 1)    zo++;
            if(p1 + p2 == 2)    oo++;
        }
        if(oo % 2 and zo == 0)  ans += 2;

        if(m % 2 and n % 2 and grid[m / 2][n / 2])  ans++;

        return ans;
    }
};
