class Solution {
    public int minFlips(int[][] grid) {
        int ans = 0;

        int possibleOne = 0;
        int countOnePrev = 0;

        if(grid.length % 2 == 1) {
            int st = 0;
            int ed = grid[0].length - 1;

            while(st < ed) {
                if(grid[grid.length / 2][st] != grid[grid.length / 2][ed]) {
                    possibleOne += 2;
                    ans++;
                } else {
                    countOnePrev += 2 * grid[grid.length / 2][st];
                }

                st++;
                ed--;
            }
        }

        if(grid[0].length % 2 == 1) {
            int st = 0;
            int ed = grid.length - 1;

            while(st < ed) {
                if(grid[st][grid[0].length / 2] != grid[ed][grid[0].length / 2]) {
                    possibleOne += 2;
                    ans++;
                } else {
                    countOnePrev += 2 * grid[st][grid[0].length / 2];
                }

                st++;
                ed--;
            }
        }

        if(grid.length % 2 == 1 && grid[0].length % 2 == 1 && grid[grid.length / 2][grid[0].length / 2] == 1)
            ans++;

        if((countOnePrev % 4) != 0) {
            int val = possibleOne - (4 - (countOnePrev % 4));
            if(val < 0) {
                ans += (countOnePrev % 4);
            }
        }

        int rowStart = 0;
        int rowEnd = grid.length - 1;

        while(rowStart < rowEnd) {

            int colStart = 0;
            int colEnd = grid[0].length - 1;

            while(colStart < colEnd) {

                int countOne = 0;

                countOne += grid[rowStart][colStart];
                countOne += grid[rowStart][colEnd];
                countOne += grid[rowEnd][colStart];
                countOne += grid[rowEnd][colEnd];

                if(countOne > 2) {
                    ans += 4 - countOne;
                } else {
                    ans += countOne;
                }

                colStart++;
                colEnd--;
            }

            rowStart++;
            rowEnd--;
        }

        return ans;
    }
}
