class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size(), m=g[0].size();
        int mx=0, ans=0, cm=0;

        if(m == 1 && n == 1)
            return g[0][0];

        if(n == 1){
            int s1=0,s2=0;
            for(int j=0;j<m/2;j++){
                if(g[0][j] != g[0][m-1-j]){
                    s1++;
                    ans++;
                } else if(g[0][j] == 1){
                    s2++;
                }
            }
            int oc = 2*s2 + (m&1 ? g[0][m/2] : 0);
            if(oc%4 == 0)
                return ans;
            if(oc%4 == 1){
                return ans+1;
            }
            if(oc%4 == 2){
                if(s1) return ans;
                else return ans+2;
            }
            if(oc%4 == 3){
                if(s1) return ans+1;
                else return ans+3;
            }
        }

        if(m == 1){
            int s1=0, s2=0;
            for(int i=0;i<n/2;i++){
                if(g[i][0] != g[n-1-i][0]){
                    s1++;
                    ans++;
                } else if(g[i][0] == 1){
                    s2++;
                }
            }
            int oc = 2*s2 + (n&1 ? g[n/2][0] : 0);
            if(oc%4 == 0)
                return ans;
            if(oc%4 == 1){
                return ans+1;
            }
            if(oc%4 == 2){
                if(s1) return ans;
                else return ans+2;
            }
            if(oc%4 == 3){
                if(s1) return ans+1;
                else return ans+3;
            }
        }

        int m0=0, m1=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int val=g[i][j];
                int cc=1;
                if(i != n-1-i){
                    val += g[n-1-i][j];
                    cc++;
                }
                if(j != m-1-j){
                    val += g[i][m-1-j];
                    cc++;
                }
                if(i != n-1-i && j != m-1-j){
                    val += g[n-1-i][m-1-j];
                    cc++;
                }
                // mx = max(mx, val);
                ans += min(val, cc-val);
                if(val > cc-val)
                    m1 += cc;
            }
        }
        // cout<<ans<<" ";

        int r1=0, r2=0;
        // middle row
        if(n&1){
            for(int j=0;j<m/2;j++){
                if(g[n/2][j] != g[n/2][m-1-j]){
                    ans++;
                    r1++;
                } else if(g[n/2][j] == 1)
                    r2 += 2;
            }
        }
        // cout<<ans<<" ";

        int c1=0, c2=0;
        // middle column
        if(m&1){
            for(int i=0;i<n/2;i++)
                if(g[i][m/2] != g[n-1-i][m/2]){
                    ans++;
                    c1++;
                } else if(g[i][m/2] == 1)
                    c2 += 2;
        }

        if(n%2 == 1 && m%2 == 1)
            cm = g[n/2][m/2];

        int val = (m1+r2+c2+cm);
        if(val%4 == 0)
            return ans;
        else if(val%4 == 1){
            return ans+1;
        } else if(val%4 == 2){
            if(r1 || c1) return ans;
            else return ans+2;
        } else if(val%4 == 3){
            if(r1 || c1) return ans+1;
            else return ans+3;
        }
        return ans;
    }
};
