class Solution {
public:
 vector<vector<vector<int> > > dp;
int ans(int i,int j,int n,int m,int r,vector<vector<int>> &grid){
    if(dp[i][j][r]!=-1){
        return dp[i][j][r];
    }
    if(i==n/2&&n%2==1&&j==m/2&&m%2==1){
       int x=grid[i][j];
       return dp[i][j][r]=min(x+ans(i+1,j,n,m,(r),grid),1-x+ans(i+1,j,n,m,(r+1)%4,grid));

    }

    if(i>n/2){
                if(r==0){
            return dp[i][j][r]=0;
        }
        return dp[i][j][r]=n*m;


    }
    if(i==n/2){
        if(n%2==0){
        if(r==0){
            return 0;
        }
        return dp[i][j][r]=n*m;
        }
        else{
            if(j==m/2){
                return dp[i][j][r]=ans(i+1,0,n,m,r,grid);
            }

        int x=grid[i][j];
        int y=grid[i][m-1-j];
        return dp[i][j][r]=min(1-x+1-y+ans(i,j+1,n,m,(r+2)%4,grid),x+y+ans(i,j+1,n,m,r,grid));
        }
    }
    if(j==m/2){
        if(m%2==0){
        return dp[i][j][r]=ans(i+1,0,n,m,r,grid);
        }


    else{
        int x=grid[i][j];
        int y=grid[n-1-i][j];
        return dp[i][j][r]=min(1-x+1-y+ans(i+1,0,n,m,(r+2)%4,grid),x+y+ans(i+1,0,n,m,r,grid));

    }

    }
    int x=grid[i][j];
    int y=grid[n-1-i][j];
    int x1=grid[n-1-i][m-1-j];
    int y1=grid[i][m-1-j];
    return dp[i][j][r]=min(1-x+1-y+1-x1+1-y1+ans(i,j+1,n,m,(r+4)%4,grid),x+y+x1+y1+ans(i,j+1,n,m,r,grid));


}
    int minFlips(vector<vector<int>>& grid) {
            vector<vector<vector<int> > > v(
        grid.size()+3, vector<vector<int> >(grid[0].size()+3, vector<int>(4,-1)));
        dp=v;

        return ans(0,0,grid.size(),grid[0].size(),0,grid);
    }
};
