class Solution {
    public int minFlips(int[][] a) {
        int n = a.length, m = a[0].length;
        int ans = 0;
        for(int i = 0; i < n / 2; i++) {
            for(int j=0; j < m/2; j++) {
                int a0=0, a1=0;
                if(a[i][j] > 0) a1++; else a0++;
                if(a[n - i - 1][j] > 0) a1++; else a0++;
                if(a[i][m - j - 1] > 0) a1++; else a0++;
                if(a[n - i - 1][m - j - 1] > 0) a1++; else a0++;
                if(a1 >= a0) {
                    ans += 4 - a1;
                    a[i][j] = a[n - i - 1][j] = a[i][m - j - 1] = a[n - i - 1][m - j - 1] = 1;
                } else {
                    ans += 4 - a0;
                    a[i][j] = a[n - i - 1][j] = a[i][m - j - 1] = a[n - i - 1][m - j - 1] = 0;
                }
            }
        }

        int nn = 0;
        if(n % 2 == 1) {
            for(int i=0;i<m/2;i++) {
                if(a[n / 2][i] != a[n / 2][m - 1 - i]) {
                    a[n / 2][i] = a[n / 2][m - 1 - i]     = 0;
                    nn++;
                }
            }
        }

        int mm = 0;
        if(m % 2 == 1) {
            for(int i=0;i<n/2;i++) {
                if(a[i][m / 2] != a[n - i - 1][m / 2]) {
                    a[i][m / 2] = a[n - i - 1][m / 2] = 0;
                    mm++;
                }
            }
        }

        if(n % 2 == 1&&m % 2 == 1 && a[n / 2][m / 2] > 0) {
            ans ++;
            a[n / 2][m / 2] = 0;
        }

        int num =0;
        for(int i=0;i<n;i++) {
            for(int j=0;j<m;j++) {
                if(a[i][j] > 0) num++;
            }
        }
        if(num % 4 != 0) {
            if(nn == 0 && mm == 0) ans += 2;
        }

        return ans + nn + mm;
    }
}
