class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int irev=n-1-i;
                int jrev=m-1-j;
                int onecount=0,zerocount=0;
                if(grid[i][j]==1)
                onecount++;
                if(grid[irev][j]==1)
                onecount++;
                if(grid[i][jrev]==1){
                    onecount++;
                }
                if(grid[irev][jrev]==1)
                onecount++;
                zerocount=4-onecount;
                ans+=onecount<zerocount?onecount:zerocount;
            }
        }
        int fault=0,onepair=0;
        if(n%2==1){
            int mid=(n/2);
            int start=0,end=m-1;
            while(start<end)
            {
                if(grid[mid][start]!=grid[mid][end])
                fault++;
                else if(grid[mid][start]==1&&grid[mid][end]==1)
                onepair++;
                start++;
                end--;
            }
        }
        if(m%2==1){
            int mid=(m/2);
            int start=0,end=n-1;
            while(start<end)
            {
                if(grid[start][mid]!=grid[end][mid])
                fault++;
                else if(grid[start][mid]==1&&grid[end][mid]==1)
                onepair++;
                start++;
                end--;
            }
        }
        if(fault==0&&onepair%2==1)
        ans+=2;
        ans+=fault;
        if(n%2==1&&m%2==1)
        {
            if(grid[n/2][m/2]==1)
            ans++;
        }
        return ans;
    }
};
