class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        int i = 0;
        int j = 0;

        int res = 0;

        for(int i=0; i<m/2; i++) {
            for(int j=0; j<n/2; j++) {
                int p = grid[i][j];
                int q = grid[i][n-1-j];
                int r = grid[m-1-i][n-1-j];
                int s = grid[m-1-i][j];

                int sum = p+q+r+s;

                res += min(sum, 4-sum);

            }
        }

        int pro = 0;
        int ones = 0;

        if(n%2) {
            int x = (n/2);
            for(int i=0; i<m/2; i++) {
                int p = grid[i][x];
                int q = grid[m-1-i][x];

                // cout << p << " " << q << endl;

                if(p==1 && q==1) ones++;
                if(p!=q) pro++;
            }
        }


        if(m%2) {
            int x = (m/2);
            for(int i=0; i<n/2; i++) {
                int p = grid[x][i];
                int q = grid[x][n-1-i];

                if(p==1 && q==1) ones++;
                if(p!=q) pro++;
            }
        }

        // cout << pro << " " << ones;

        if(ones%2) {
            if(pro) {
                res += pro;
            }
            else {
                res += (pro + 2);
            }
        }
        else {
            res += pro;
        }

        if(m%2 && n%2) res += grid[m/2][n/2];

        return res;
    }
};
