class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        h = []
        dp = [[0]*len(grid[0]) for _ in range(len(grid))]

        res = 0
        for i in range(len(grid)//2):
            for j in range(len(grid[0])//2):
                lu, ru, ld, rd = grid[i][j], grid[i][-j-1], grid[-i-1][j], grid[-i-1][-j-1]
                if sum([lu, ru, ld, rd]) > 2:
                    changed_num = 1
                else:
                    changed_num = 0

                if grid[i][j] != changed_num:
                    grid[i][j] = changed_num
                    res += 1
                if grid[i][-j-1] != changed_num:
                    grid[i][-j-1] = changed_num
                    res += 1
                if grid[-i-1][j] != changed_num:
                    grid[-i-1][j] = changed_num
                    res += 1
                if grid[-i-1][-j-1] != changed_num:
                    grid[-i-1][-j-1] = changed_num
                    res += 1

        same_ones = 0
        different_num = 0
        if len(grid) % 2 == 1:
            for j in range(len(grid[0])//2):
                if grid[len(grid)//2][j] != grid[len(grid)//2][-j-1]:
                    different_num += 1
                elif grid[len(grid)//2][j] == 1:
                    same_ones += 2

        if len(grid[0]) % 2 == 1:
            for i in range(len(grid)//2):
                if grid[i][len(grid[0])//2] != grid[-i-1][len(grid[0])//2]:
                    different_num += 1
                elif grid[i][len(grid[0])//2] == 1:
                    same_ones += 2

        middle_one = 0
        if len(grid) % 2 == 1 and len(grid[0]) % 2 == 1 and grid[len(grid)//2][len(grid[0])//2] == 1:
            middle_one += 1

        if same_ones % 4 == 0:
            return res + different_num + middle_one
        else:   # same_ones % 4 == 2
            if different_num:
                return res + different_num + middle_one
            else:
                return res + different_num + middle_one + 2
