class Solution {
public:

int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0, m2 = 0;
        bool yes = 0;
        for(int i = 0; i < (n+1)/2; i++){
            for(int j = 0; j < (m+1)/2; j++){
                set < pair <int,int> > s;
                s.insert({i,j}); s.insert({n-i-1,j});
                s.insert({i,m-1-j}); s.insert({n-i-1,m-j-1});
                if(s.size() == 4){
                    int c = 0;
                    if(grid[i][j] == 1) c++;
                    if(grid[n-i-1][j] == 1) c++;
                    if(grid[i][m-j-1] == 1) c++;
                    if(grid[n-i-1][m-j-1] == 1) c++;
                    if(c == 1 || c == 3) ans += 1;
                    if(c == 2) ans += 2;
                }
                else if(s.size() == 2){
                    if(i == n-i-1){
                        int c = 0;
                        if(grid[i][j] == 1) c++;
                        if(grid[i][m-j-1] == 1) c++;
                        if(c == 2) {
                            m2 += 2;
                        }
                        if(c == 1){
                            yes = 1; ans+=1;
                        }
                    }
                    else{
                        int c = 0;
                        if(grid[i][j] == 1) c++;
                        if(grid[n-i-1][j] == 1) c++;
                        if(c == 2) {
                            m2 += 2;
                        }
                        if(c == 1){
                            yes = 1; ans+=1;
                        }
                    }
                }
                else{
                    if(grid[i][j] == 1) ans++;
                }
            }
        }
        if(!yes && m2%4 != 0) ans += 2;
        return ans;
    }
};
