class Solution {
public:
    // int find(int i, int j, int n, int m, vector<vector<int>>& grid,
    // unordered_map <pair<int,int>> &umap) {
    //     int n = grid.size(), m = grid[0].size();

    //     return cnt;
    // }
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        vector<vector<int>> mat(n, vector<int> (m, 0));

        int ans = 0;

        int x =0 ,  y =0, z = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (mat[i][j]) {
                    continue;
                } else {
                    // cout<<i<<" "<<j<<" | ";
                    int cnt = 0, tot = 1;

                    cnt += grid[i][j];
                    mat[i][j]++;

                    if (mat[i][m-j-1] == 0) {
                        cnt += grid[i][m - j - 1];
                        mat[i][m-j-1]++;
                        tot++;
                        // cout<<"(2)";
                    }
                    if (mat[n-i-1][j] == 0) {
                        cnt += grid[n - i - 1][j];
                        mat[n-i-1][j]++;
                        tot++;
                        // cout<<"(3)";
                    }
                    if (mat[n-i-1][m-j-1] == 0) {
                        cnt += grid[n - i - 1][m - j - 1];
                        mat[n-i-1][m-j-1]++;
                        tot++;
                        // cout<<"(4)";
                    }

                    // cout <<"i = "<< i << " j = " << j << " cnt = " << cnt << " tot = "<<tot<<"\n";

                    if (tot == 1 && cnt) ans++;
                    if (tot == 4) ans += min(cnt, 4 - cnt);
                    if (tot == 2) {
                        if (cnt ==1)  {
                            ans += 1;
                            y++;
                        } else if (cnt == 0) {
                            z++;
                        }
                        else if (cnt) {
                            x++; 
                        }
                    }
                }
            }
        }

        // cout<<"ans = "<<ans<<" x = "<<x<<" y = "<<y<<" z = "<<z<<"\n";

        if (x&1 && !y) {
            ans += 2;
        }

        return ans;
    }
};
