from typing import List


class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        x = 0
        a = grid

        for i in range(len(a)//2):
            for j in range(len(a[0])//2):
                b = [0,0]
                b[a[i][j]]+=1
                b[a[-1-i][j]]+=1
                b[a[i][-1-j]]+=1
                b[a[-1-i][-1-j]]+=1
                x+=4-max(b)
        middle = 0
        ones=0


        if len(a)%2==1:
            for j in range(len(a[0])//2):
                if a[len(a)//2][j]!=a[len(a)//2][-j-1]:
                    ones+=1
                    middle+=1
                else:
                    if a[len(a)//2][j] == 1:
                        ones += 2
        center = 0
        cvalid = False
        if len(a[0])%2==1:
            for i in range(len(a)//2):
                if a[i][len(a[0])//2]  != a[-i-1][len(a[0])//2]:
                    ones+=1
                    middle+=1
                else:
                    if a[i][len(a[0])//2]==1:
                        ones+=2
            if len(a)%2==1:
                if a[len(a)//2][len(a[0])//2]==1:
                    center = 1
                    cvalid = True
                    x+=1


        if (ones+middle) %4==0:
            return x+middle

        else:
            if middle>0:
                return x+middle
            else:
                return x+2
