class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int extra = 0, extraSingle = 0;
        int tp = 0;
        for (int i=0; i<(grid.size()+1)/2; i++){
            int i1 = 0, i2 = grid[i].size()-1;
            while (i1<=i2){
                vector<int> v = {0,0};
                v[grid[i][i1]]++;
                if (i1!=i2) v[grid[i][i2]]++;
                if (i!=(grid.size()-i-1)){
                    v[grid[grid.size()-i-1][i1]]++;
                    if (i1!=i2) v[grid[grid.size()-i-1][i2]]++;
                }

                if (v[0]+v[1]==1 && v[0]==1) extraSingle++;

                // cout<<i<<" "<<i1<<" "<<i2<<" "<<v[0]<<" "<<v[1]<<endl;
                // cout<<v[0]<<" "<<v[1]<<endl;
                if (v[0]==0 || v[1]==0){
                    i1++; i2--;
                    continue;
                }

                if (v[0]+v[1]==2) extra++;

                tp += min(v[0],v[1]);

                if (v[0]>=v[1]){
                    grid[i][i1]=0;
                    grid[i][i2]=0;
                    grid[grid.size()-i-1][i1]=0;
                    grid[grid.size()-i-1][i2]=0;
                } else {
                    grid[i][i1]=1;
                    grid[i][i2]=1;
                    grid[grid.size()-i-1][i1]=1;
                    grid[grid.size()-i-1][i2]=1;
                }

                i1++; i2--;
            }
        }

        // cout<<tp<<endl;

        // for (int i=0; i<grid.size(); i++){
        //     for (int j=0; j<grid[0].size(); j++){
        //         cout<<grid[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }

        int ct = 0;
        for (int i=0; i<grid.size(); i++){
            for (int j=0; j<grid[0].size(); j++){
                ct+=(grid[i][j]==1);
            }
        }

        // cout<<extra<<endl;

        if (ct%4==0){
            return tp;
        } else if (ct%4==1){
            return tp+1;
        } else if (ct%4==3){
            if (extraSingle) return 1;
            else if (extra) return tp+1;
            else return tp+3;
        } else {
            if (extra>0) return tp;
            else return tp+2;
        }
        return tp;
    }
};
