class Solution {
public:
    // 1,2 -> 0 , 3,4->1, 
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m=grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int s = grid[i][j] + grid[i][m-j-1] + grid[n-i-1][j] + grid[n-i-1][m-j-1];
                ans += min(s , 4-s);
            }
        }
        int cnt = 0;
        int cnt2 = 0;
        if(n % 2 != 0){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j] != grid[n/2][m-j-1])cnt++;
                else cnt2+=grid[n/2][j];
            }
        }
        if(m % 2 != 0){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-i-1][m/2])cnt++;
                else cnt2 += grid[i][m/2];
            }
        }
        if(n % 2 && m % 2 && grid[n/2][m/2]){
            ans++;
        }
        if(cnt2 % 2 && cnt == 0) ans+=2;
        ans += cnt;

        return ans;
    }
};
